import React, { useState } from 'react';
import axios from 'axios';

import Copyright from './Copyright';
import Alert from './Alert';

import { useStyles } from '../styles/homeStyle';

import { 
    Avatar,
    Button,
    CssBaseline,
    TextField,
    Link,
    Paper,
    Box,
    Grid,
    Typography,
    Snackbar, 
} from '@mui/material';

export default function Home(props) {  
    const classes = useStyles();

    const [number, setNumber] = useState();
    const [message, setMessage] = useState();
    const [open, setOpen] = useState();

    const handleOpen = () => {
        setOpen(true);
    };
    
    const handleClose = () => {
        setOpen(false);
    };

    const handleSubmit = async (e) => {  
        e.preventDefault();
        const data = {
            number: 9
        }

        const headers = {
            'Content-Type': 'application/json'
        }
        
        console.log(data);

        axios.post(process.env.REACT_APP_API_URL + '/', data, { headers: headers })
        .then((response) => {
            setNumber(response.data.number);
        });
    }    

    return (
        <div>
            <Grid container component="main" className={classes.root}>
                <PopupAlert 
                    open={open}
                    handleClose={handleClose}
                    handleClickOpen={handleClickOpen}
                    text="Hoppá!"
                />
                <CssBaseline />
                    <Grid item xs={false} sm={4} md={7} className={classes.image} />
                    <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                        <div className={classes.paper}>
                            <Avatar className={classes.avatar}>
                                <LockOutlinedIcon />
                            </Avatar>
                            <Typography component="h1" variant="h5">
                                Jelentkezési felület
                            </Typography>
                            <form className={classes.form} onSubmit={handleSubmit} noValidate>
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="email"
                                    type="email"
                                    label="E-mail cím"
                                    name="email"
                                    onChange={e => setEmail(e.target.value)}
                                    autoFocus
                                />
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    name="password"
                                    label="Jelszó"
                                    type="password"
                                    id="password"
                                    onChange={e => setPassword(e.target.value)}
                                />
                                <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                className={classes.submit}
                                >
                                Bejelentkezés
                                </Button>
                                <Grid container>
                                <Grid item xs>
                                    <Link href="" onClick={handleChange2} variant="body2">
                                    Jelszó megváltoztatása
                                    </Link>
                                </Grid>
                                {/*<Grid item>
                                    <Link href="" onClick={handleChange} variant="body2">
                                    Nem tudsz bejelentkezni? Regisztrálj!
                                    </Link>
                                </Grid>*/}
                                </Grid>
                                <Box mt={5}>
                                <Copyright />
                                </Box>
                                <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                                <ErrorAlert onClose={handleClose} severity="error"> {message} </ErrorAlert>
                                </Snackbar>
                            </form>
                        </div>
                    </Grid>
             </Grid>
        </div>
    );
  }
