import React, { useState, useEffect } from 'react';
import axios from 'axios';

import Copyright from './Copyright';
import Alert from './Alert';

import { createTheme, ThemeProvider } from '@mui/material/styles';

import { 
    Avatar,
    Button,
    CssBaseline,
    TextField,
    Paper,
    Box,
    Grid,
    Typography,
    InputLabel,
    Select
} from '@mui/material';

import EventNoteIcon from '@mui/icons-material/EventNote';

const theme = createTheme();

export default function Home() {  
    const classes = useStyles();

    const [name, setName] = useState("");
    const [category, setCategory] = useState("");
    const [categories, setCategories] = useState([]);
    const [event, setEvent] = useState("");
    const [events, setEvents] = useState([]);
    const [message, setMessage] = useState("");
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
        setOpen(true);
    };
    
    const handleClose = () => {
        setOpen(false);
    };

    const handleSubmit = async (e) => {  
        e.preventDefault();
        const data = {
            name: name,
            category: category,
            event: event
        }

        const headers = {
            'Content-Type': 'application/json'
        }
        
        console.log(data);

        axios.post(process.env.REACT_APP_API_URL + '/', data, { headers: headers })
        .then((response) => {
            setMessage(response.data.message);
        });
    }    

    useEffect(() => {
        const headers = {
            'Content-Type': 'application/json',
        }
    
        axios.post(process.env.REACT_APP_API_URL + '/classes', {}, {headers: headers})
            .then((response) => {
                setCategories(response.data.categories);
        });
    },[])

    useEffect(() => {
        const headers = {
            'Content-Type': 'application/json',
        }
    
        axios.post(process.env.REACT_APP_API_URL + '/events', {}, {headers: headers})
            .then((response) => {
            if(!response.data.code){
                setEvents(response.data.events);
            }
            else
            {
                console.log(response.data.message);
            }
        });
    },[])

    return (
        <div>
            <ThemeProvider theme={theme}>
                <Grid container component="main" sx={{ height: '100vh' }}>
                    <CssBaseline />
                    <Grid
                    item
                    xs={false}
                    sm={4}
                    md={7}
                    sx={{
                        backgroundImage: 'url(https://source.unsplash.com/random)',
                        backgroundRepeat: 'no-repeat',
                        backgroundColor: (t) =>
                        t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                    }}
                    />
                    <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <Box
                        sx={{
                        my: 8,
                        mx: 4,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        }}
                    >
                        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                        <LockOutlinedIcon />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                        Sign in
                        </Typography>
                        <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            label="Email Address"
                            name="email"
                            autoComplete="email"
                            autoFocus
                        />
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            name="password"
                            label="Password"
                            type="password"
                            id="password"
                            autoComplete="current-password"
                        />
                        <FormControlLabel
                            control={<Checkbox value="remember" color="primary" />}
                            label="Remember me"
                        />
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                        >
                            Sign In
                        </Button>
                        <Grid container>
                            <Grid item xs>
                            <Link href="#" variant="body2">
                                Forgot password?
                            </Link>
                            </Grid>
                            <Grid item>
                            <Link href="#" variant="body2">
                                {"Don't have an account? Sign Up"}
                            </Link>
                            </Grid>
                        </Grid>
                        <Copyright sx={{ mt: 5 }} />
                        </Box>
                    </Box>
                    </Grid>
                </Grid>
                </ThemeProvider>
            <Grid container component="main" className={classes.root}>
                <Alert 
                    open={open}
                    handleClose={handleClose}
                    handleClickOpen={handleOpen}
                    text={message}
                />
                <CssBaseline />
                <Grid item xs={false} sm={4} md={7} className={classes.image} />
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <div className={classes.paper}>
                        <Avatar className={classes.avatar}>
                            <EventNoteIcon />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Jelentkezési felület
                        </Typography>
                        <form className={classes.form} onSubmit={handleSubmit} noValidate>
                            <TextField
                                variant="outlined"
                                margin="normal"
                                required
                                fullWidth
                                id="name"
                                type="text"
                                label="Tanuló neve"
                                name="name"
                                onChange={e => setName(e.target.value)}
                                autoFocus
                            />
                            <InputLabel htmlFor="category"> Osztály </InputLabel>
                            <Select 
                                native 
                                fullWidth
                                required
                                variant="outlined"
                                name="category"
                                id="category"
                                onChange={ e => setCategory(e.target.value)}
                            >
                                <option aria-label="None" value="" />
                                { 
                                    categories.map((cat,i) => (
                                        <option key={i} value={cat}>{categories}</option>
                                ))}
                            </Select>
                            <InputLabel htmlFor="event"> Osztály </InputLabel>
                            <Select 
                                native 
                                fullWidth
                                required
                                variant="outlined"
                                name="event"
                                id="event"
                                onChange={ e => setEvent(e.target.value)}
                            >
                                <option aria-label="None" value="" />
                                { 
                                    events.map((ev,i) => (
                                        <option key={i} value={ev}>{ev}</option>
                                ))}
                            </Select>
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                color="primary"
                                className={classes.submit}
                            >
                                Jelentkezés
                            </Button>
                            <Box mt={5}>
                                <Copyright />
                            </Box>
                        </form>
                    </div>
                </Grid>
             </Grid>
        </div>
    );
  }
